import React, {useState} from "react";

export default function SignUp(props) {
    const [username, setUsername] = useState("")
    const [email, setEmail] = useState("")
    const [password, setPassword] = useState("")

    const userSignUp = async () => {
        const body = {
            username: userna,
            email: "this@gmail.com",
            password: "as12AS!!"
        }

        let itt = await fetch("http://localhost:5000/signup", {
            method: "post",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(body)
        })

        console.log(itt)
    }

    return (
        <div>
            {/* <form> */}
                <input type="text" value={username} placeholder="username" onChange={(e) => {
                    setUsername(e.target.value)
                }}></input>
            
                <input type="email" value={email} placeholder="email" onChange={(e) => {
                    setEmail(e.target.value)
                }}></input>
            
                <input type="text" value={password} placeholder="password" onChange={(e) => {
                    setPassword(e.target.value)
                }}></input>
                <button onClick={() => userSignUp()}>Sign up</button>
            {/* </form> */}
            <div>
                <p>Password must include:</p>
                <p>At least eight characters</p>
                <p>At least one lowercase letter</p>
                <p>At least one uppercase letter</p>
                <p>At least one number</p>
                <p>At least one special character</p>
            </div>
        </div>
    )
}
