import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

export default function SignUp(props) {
    const [username, setUsername] = useState("")
    const [email, setEmail] = useState("")
    const [password, setPassword] = useState("")

    const redirect = useNavigate()

    const userSignUp = async (e) => {
        e.preventDefault()
        try {
            const body = {
                username: username,
                email: email,
                password: password
            }

            let itt = await fetch("http://localhost:5000/signup", {
                method: "post",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(body)
            })

            if (itt.status === 200) {
                redirect("/you")
            }
        }
        catch (error) {
            console.error(error.message)
        }
    }

    return (
        <div>
            <form onSubmit={(e) => userSignUp(e)}>
                <input type="text" value={username} placeholder="username" onChange={(e) => {
                    setUsername(e.target.value)
                }}></input>
            
                <input type="email" value={email} placeholder="email" onChange={(e) => {
                    setEmail(e.target.value)
                }}></input>
            
                <input type="text" value={password} placeholder="password" onChange={(e) => {
                    setPassword(e.target.value)
                }}></input>
                <button>Sign up</button>
            </form>
            <div>
                <p>Been Here? <Link to="/login">Login ere</Link> </p>
            </div>
            <div>
                <p>Password must include:</p>
                <p>At least eight characters</p>
                <p>At least one lowercase letter</p>
                <p>At least one uppercase letter</p>
                <p>At least one number</p>
                <p>At least one special character</p>
            </div>
        </div>
    )
}
