import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

export default function SignUp(props) {
    const [username, setUsername] = useState("")
    const [email, setEmail] = useState("")
    const [password, setPassword] = useState("")

    const redirect = useNavigate()

    const userSignUp = async (e) => {
        e.preventDefault()
        try {
            const body = {
                email: email,
                password: password
            }

            let itt = await fetch("http://localhost:5000/login")
                .then(response => response.json())
                .then()

            if (itt.status === 200) {
                redirect("/you")
            }
        } catch (error) {
            console.error(error.message)
        }
    }

    return (
        <div>
            <form onSubmit={(e) => userSignUp(e)}>
                <input type="email" value={email} placeholder="email" onChange={(e) => {
                    setEmail(e.target.value)
                }}></input>
            
                <input type="text" value={password} placeholder="password" onChange={(e) => {
                    setPassword(e.target.value)
                }}></input>
                <button>Log in</button>
            </form>
            <div>
                <p>Don't have an account? <Link to="/signup">Make on here!</Link> </p>
            </div>
        </div>
    )
}
