import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function SignUp(props) {
    const [username, setUsername] = useState("")
    const [email, setEmail] = useState("")
    const [password, setPassword] = useState("")

    const redirect = useNavigate()

    const userSignUp = async () => {
        try {
            const body = {
                username: username,
                email: email,
                password: password
            }

            let itt = await fetch("http://localhost:5000/signup", {
                method: "post",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(body)
            })

            console.log(itt)
            if (itt.status === 200) {
                redirect("/you")
            }
        } catch (error) {
            console.error(error.message)
        }
    }

    return (
        <div>
            <form on>
                <input type="text" value={username} placeholder="username" onChange={(e) => {
                    setUsername(e.target.value)
                }}></input>
            
                <input type="email" value={email} placeholder="email" onChange={(e) => {
                    setEmail(e.target.value)
                }}></input>
            
                <input type="text" value={password} placeholder="password" onChange={(e) => {
                    setPassword(e.target.value)
                }}></input>
                <button onClick={() => userSignUp()}>Sign up</button>
            </form>
            <div>
                <p>Password must include:</p>
                <p>At least eight characters</p>
                <p>At least one lowercase letter</p>
                <p>At least one uppercase letter</p>
                <p>At least one number</p>
                <p>At least one special character</p>
            </div>
        </div>
    )
}
