import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

export default function SignUp(props) {
    const [username, setUsername] = useState("")
    const [email, setEmail] = useState("")
    const [password, setPassword] = useState("")

    const redirect = useNavigate()

    const userSignUp = async (e) => {
        e.preventDefault()
        try {
            const body = {
                username: username,
                email: email,
                password: password
            }

            let itt = await fetch("http://localhost:5000/signup", {
                method: "post",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(body)
            })

            if (itt.status === 200) {
                redirect("/you")
            }
        }
        catch (error) {
            console.error(error.message)
        }
    }

    return (
        <div>
            <div class="dropdown-menu">
            <form class="px-4 py-3">
                <div class="form-group">
                <label for="exampleDropdownFormEmail1">Email address</label>
                <input type="email" class="form-control" id="exampleDropdownFormEmail1" placeholder="email@example.com" />
                </div>
                <div class="form-group">
                <label for="exampleDropdownFormPassword1">Password</label>
                <input type="password" class="form-control" id="exampleDropdownFormPassword1" placeholder="Password" />
                </div>
                <div class="form-check">
                <input type="checkbox" class="form-check-input" id="dropdownCheck" />
                <label class="form-check-label" for="dropdownCheck">
                    Remember me
                </label>
                </div>
                <button type="submit" class="btn btn-primary">Sign in</button>
            </form>
            <div class="dropdown-divider"></div>
            <a class="dropdown-item" href="#">New around here? Sign up</a>
            <a class="dropdown-item" href="#">Forgot password?</a>
            </div>
            {/* <form onSubmit={(e) => userSignUp(e)}>
                <input type="text" value={username} placeholder="username" onChange={(e) => {
                    setUsername(e.target.value)
                }}></input>
            
                <input type="email" value={email} placeholder="email" onChange={(e) => {
                    setEmail(e.target.value)
                }}></input>
            
                <input type="password" value={password} placeholder="password" onChange={(e) => {
                    setPassword(e.target.value)
                }}></input>
                <button>Sign up</button>
            </form>
            <div>
                <p>Already with us? <Link to="/login">Login here!</Link> </p>
            </div>
            <div>
                <p>Password must include:</p>
                <p>At least eight characters</p>
                <p>At least one lowercase letter</p>
                <p>At least one uppercase letter</p>
                <p>At least one number</p>
                <p>At least one special character</p>
            </div> */}
        </div>
    )
}
