import React, {useState, useEffect} from 'react'

export default function EditTask({task}) {
    const [updateWith, setUpdateWith] = useState("nougat")

    const updateTask = async (event) => {
        event.preventDefault()

        try {
            const body = {task: "updateWith"}
            await fetch(`http://localhost:5000/todolist/13`, {
                method: "put",
                headers: { "Content-Type": "applications/json" },
                body: JSON.stringify(body)
            })

            window.location = "/"
        } catch (error) {
            console.error(error.message)
        }
    }

    return (
        // <div>
        //     <form onSubmit={updateTask}>
        //         {/* <input type="text"></input> */}
        //         <button>✍</button>                
        //     </form>
        // </div>
        
        <div>
            <button type="button" className="btn btn-primary" data-bs-toggle="modal" data-bs-target={`#T${task.taskid}`}>
            ✍
            </button>

            <div className="modal fade" id={`T${task.taskid}`} tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
            <div className="modal-dialog">
                <div className="modal-content">
                <div className="modal-header">
                    <h5 className="modal-title" id="exampleModalLabel">Edit this task</h5>
                    <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                </div>
                <div className="modal-body">
                    <input value={task.task} onChange={(event) =>
                        setUpdateWith(event.target.value)
                    }></input>
                </div>
                <div className="modal-footer">
                    <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                    <button type="button" className="btn btn-primary">Save changes</button>
                </div>
                </div>
            </div>
            </div>
        </div>
    )
}
