import React, {useState} from 'react'

export default function AddTask() {
    const [newTask, setNewTask] = useState("")

    const addTask = async (event) => {
        event.preventDefault()

        try {
            const itt = await fetch("http://localhost:5000/todolist/", {
                method: "post",
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    "task": newTask
                })
            })
            console.log(itt)
        } catch (error) {
            console.error(error)
        }
    }

    return (
        <div>
            {/* <form onSubmit={ () => addTask() }> */}
                <input id="task-input" type="text" value={newTask} placeholder="What do you need to do?"
                    onChange={ event =>
                        setNewTask(event.target.value)
                    }></input>
                <button>Add Task</button>
            {/* </form> */}
        </div>
    )
}
